public class Cp1251ToUtf8 {
    public static String cp1251ToUtf8(String cp1251String) {
        // Get the bytes of the string using CP-1251 encoding
        byte[] cp1251Bytes = cp1251String.getBytes(Charset.forName("Cp1251"));

        // Create a new string using the bytes and UTF-8 encoding
