import chardet

def convert_cp1251_to_utf8(cp1251_string):
    """
    Convert a string from CP-1251 to UTF-8.

    Args:
        cp1251_string (str): The string to be converted.

    Returns:
        str: The converted string in UTF-8.
    """
    # Detect the encoding of the input string
    detected_encoding = chardet.detect(cp1251_string.encode())['encoding']

    # If the detected encoding is CP-1251, convert the string to UTF-8
    if detected_encoding == 'cp1251':
        return cp1251_string.encode('cp1251').decode('utf-8')
    else:
        return cp1251_string

