import shutil
import os

def copy_file(srcfile, dstdir):
    """
    Copy file `srcfile` to directory `dstdir`.

    Args:
        srcfile (str): Path to the source file.
        dstdir (str): Path to the destination directory.

    Raises:
        FileNotFoundError: If `srcfile` does not exist.
        NotADirectoryError: If `dstdir` is not a directory.
    """
    if not os.path.exists(srcfile):
        raise FileNotFoundError(f"Source file '{srcfile}' does not exist.")
    if not os.path.isdir(dstdir):
        raise NotADirectoryError(f"Destination '{dstdir}' is not a directory.")

    shutil.copy(srcfile, dstdir)

