#!/bin/bash

copy_file() {
    local srcfile="$1"
    local dstdir="$2"

    if [ ! -f "$srcfile" ]; then
        echo "Error: Source file '$srcfile' does not exist." >&2
        return 1
    fi
    if [ ! -d "$dstdir" ]; then
        echo "Error: Destination '$dstdir' is not a directory." >&2
        return 1
    fi

    local filename="$(basename "$srcfile")"
    local destfile="$dstdir/$filename"
    cp "$srcfile" "$destfile"
}

