import json

def find_value_in_nested_json(data, target_key):
    if isinstance(data, dict):
        for key, value in data.items():
            if key == 'B':
                for sub_key, sub_value in value.items():
                    if sub_key == target_key:
                        return sub_value
            else:
                result = find_value_in_nested_json(value, target_key)
                if result is not None:
                    return result
    elif isinstance(data, list):
        for item in data:
            result = find_value_in_nested_json(item, target_key)
            if result is not None:
                return result
    return None

