```java
import java.util.ArrayList;
import java.util.List;

public class Main {
    public static List<Integer> findNInMiddle(String s) {
        // Find all 'n's in the string
        List<Integer> nIndices = new ArrayList<>();
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == 'n') {
                nIndices.add(i);
            }
        }

        // Filter out 'n's at the start and end of the string
        List<Integer> middleNIndices = new ArrayList<>();
        for (int i : nIndices) {
            if (0 < i && i < s.length() - 1) {
                middleNIndices.add(i);
            }
        }

        return middleNIndices;
    }

    public static void main(String[] args) {
        String s = "banana";
        System.out.println(findNInMiddle(s));  // Output: [2, 3, 4]
    }
}
