```c
#include <stdio.h>
#include <stdlib.h>

char* read_file_without_newlines(const char* file_path) {
    FILE* file = fopen(file_path, "r");
    if (!file) {
        printf("File %s not found.\n", file_path);
        return NULL;
    }

    fseek(file, 0, SEEK_END);
    long file_size = ftell(file);
    rewind(file);

    char* content = (char*)malloc(file_size + 1);
    if (!content) {
        fclose(file);
        return NULL;
    }

    fread(content, 1, file_size, file);
    content[file_size] = '\0';  // Null-terminate the string

    fclose(file);
    return content;
}

int main() {
    const char* file_path = "example.txt";
    char* content = read_file_without_newlines(file_path);
    if (content) {
        printf("%s\n", content);
        free(content);
    }
    return 0;
}
