def split_string_into_words_and_whitespace(input_string):
    result = []
    word = ""
    for char in input_string:
        if char.isalnum():
            if word and not word[-1].isalnum():
                result.append(word)
                word = char
            else:
                word += char
        else:
            if word:
                result.append(word)
                word = ""
            result.append(char)
    if word:
        result.append(word)
    return result

