def get_nth_item(generator, n):
    """
    Get the nth item from a generator.

    Args:
        generator: A generator object.
        n (int): The index of the item to retrieve (1-indexed).

    Returns:
        The nth item from the generator, or None if the generator is exhausted.
    """
    for _ in range(n - 1):
        try:
            next(generator)
        except StopIteration:
            return None
    try:
        return next(generator)
    except StopIteration:
        return None

# Example usage:
my_generator = (x for x in range(10))  # Create a generator that yields numbers from 0 to 9
print(get_nth_item(my_generator, 5))  # Output: 4
